pragma solidity 0.8.19;

import "../BaseTxBuilderOpen.sol";
import "./IOptionMarket.sol";
import "./IOptionToken.sol";

contract TxBuilderOpenLyra is BaseTxBuilderOpen {

    address public optionMarket; // Address of Lyra ETH contract
    address public optionToken; // Address of Lyra ETH ERC721 token contract

    // Constructor
    constructor(
        address _optionMarket,
        address _optionToken
    ) {
        optionMarket = _optionMarket;
        optionToken = _optionToken;
    }

    // Event emitted when a position is opened using Lyra protocol
    event OpenPositionByLyra(
        uint256 indexed buildID,
        uint256 strikeId,
        uint256 positionId,
        uint256 iterations,
        IOptionMarket.OptionType optionType,
        uint256 amount,
        uint256 setCollateralTo,
        uint256 minTotalCost,
        uint256 maxTotalCost,
        address referrer,
        uint256 tokenID
    );

    // Approve maximum spending limits for tokens used in the contract
    function allApprove() external {
        IOptionMarket(optionMarket).quoteAsset().approve(optionMarket, type(uint256).max);
        IOptionMarket(optionMarket).baseAsset().approve(optionMarket, type(uint256).max);
    }

    function calculateAmount(
        bytes memory parameters
    ) public view override returns (
        address token,
        uint256 amount
    ) {
        (
            IOptionMarket.TradeInputParameters memory params
        ) = decodeFromLyra(parameters);
        // Calculate premium amount and determine Lyra asset based on option type
        if (params.optionType == IOptionMarket.OptionType.LONG_CALL || params.optionType == IOptionMarket.OptionType.LONG_PUT) {
            amount = params.maxTotalCost / (1e18 / 10 ** IOptionMarket(optionMarket).quoteAsset().decimals());
            token = address(IOptionMarket(optionMarket).quoteAsset());
        } else if (params.optionType == IOptionMarket.OptionType.SHORT_CALL_QUOTE || params.optionType == IOptionMarket.OptionType.SHORT_PUT_QUOTE) {
            amount = params.setCollateralTo / (1e18 / 10 ** IOptionMarket(optionMarket).quoteAsset().decimals());
            token = address(IOptionMarket(optionMarket).quoteAsset());
        } else if (params.optionType == IOptionMarket.OptionType.SHORT_CALL_BASE) {
            amount = params.setCollateralTo / (1e18 / 10 ** IOptionMarket(optionMarket).baseAsset().decimals());
            token = address(IOptionMarket(optionMarket).baseAsset());
        }
    }

    function _processTx(
        bytes memory parameters,
        uint256 buildID,
        address user
    ) override internal {
        (
            IOptionMarket.TradeInputParameters memory params
        ) = decodeFromLyra(parameters);

        (address lyraAsset, uint256 premium) = calculateAmount(parameters);

        // Transfer premium amount from sender to the contract
        ERC20(lyraAsset).transferFrom(msg.sender, address(this), premium);

        // Get the next available ERC721 token ID
        uint256 id = IOptionToken(optionToken).nextId();

        // Open the position using the Lyra contract
        IOptionMarket(optionMarket).openPosition(params);

        // Transfer back remaining tokens to the sender
        ERC20(lyraAsset).transfer(msg.sender, ERC20(lyraAsset).balanceOf(address(this)));

        // Transfer ERC721 token representing the option to the sender
        IOptionToken(optionToken).transferFrom(address(this), user, id);
        if (params.optionType == IOptionMarket.OptionType.SHORT_CALL_BASE) {
            IOptionMarket(optionMarket).quoteAsset().transfer(user, IOptionMarket(optionMarket).quoteAsset().balanceOf(address(this)));
        }

        // Emit the OpenPositionByLyra event with relevant parameters
        emit OpenPositionByLyra(
            buildID,
            params.strikeId,
            params.positionId,
            params.iterations,
            params.optionType,
            params.amount,
            params.setCollateralTo,
            params.minTotalCost,
            params.maxTotalCost,
            params.referrer,
            id
        );
    }

    // Encode TradeInputParameters struct into bytes
    function encodeFromLyra(IOptionMarket.TradeInputParameters memory params) external pure returns (bytes memory paramData) {
        return abi.encode(params);
    }

    function decodeFromLyra(bytes memory paramData) public pure returns (IOptionMarket.TradeInputParameters memory params) {
        (
            params
        ) = abi.decode(paramData, (
            IOptionMarket.TradeInputParameters
        ));
    }
}

contracts/RoleManager.sol.content:
// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract RoleManager is Ownable, AccessControl {
    bytes32 public constant TRADER_ROLE = keccak256("TRADER_ROLE");

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setRoleAdmin(TRADER_ROLE, DEFAULT_ADMIN_ROLE);
    }

    // EXTERNAL

    function changeRole(address value, bytes32 role, bool state) external {
        if (state) {
            grantRole(role, value);
        } else {
            revokeRole(role, value);
        }
    }

    // DEFAULT_ADMIN_ROLE

    function changeAdminRole(bytes32 role, bytes32 adminRole) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "msg.sender does not own the role");
        _setRoleAdmin(role, adminRole);
    }

    function renounceAdminRole(bytes32 role) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "msg.sender does not own the role");
        require(getRoleAdmin(role) == DEFAULT_ADMIN_ROLE, "non-default admin role");
        _setRoleAdmin(role, role);
    }
}

